/**
 * The access token model
 *
 * @param  {String}     token       The randomly generated string
 * @param  {String}     userId      The ID of the user for which this token is valid
 * @param  {String}     clientId    The ID of the client for which this token was granted
 */
var AccessToken = module.exports.AccessToken = function(token, userId, clientId) {
    var that = {};
    that.token = token;
    that.userId = userId;
    that.clientId = clientId;
    return that;
};

/**
 * The client object
 *
 * @param  {String}     id              The ID of the client
 * @param  {String}     displayName     The display name of the client
 * @param  {String}     secret          A randomly generated secret for this client
 * @param  {String}     userId          The ID of the client who should own this client
 */
var Client = module.exports.Client = function(id, displayName, secret, userId) {
    var that = {};
    that.id = id;
    that.displayName = displayName;
    that.secret = secret;
    that.userId = userId;
    return that;
};


var AuthorizationCode = module.exports.AuthorizationCode = function(code, userId, clientId, redirectUri) {
    var that = {};
    that.code = code;
    that.userId = userId;
    that.clientId = clientId;
    that.redirectUri = redirectUri;
    return that;
};
