var passport = require('passport');

var OAE = require('oae-util/lib/oae');

var AuthenticationConstants = require('oae-authentication/lib/constants').AuthenticationConstants;
var AuthenticationUtil = require('oae-authentication/lib/util');

/**
 * @REST postAuthTwitter
 *
 * Redirect the user to Twitter for authentication. When complete, Twitter
 * will redirect the user back to the application at
 * /api/auth/twitter/callback
 *
 * @Server      tenant
 * @Method      POST
 * @Path        /auth/twitter
 */
OAE.tenantRouter.on('post', '/api/auth/twitter', function(req, res, next) {
    // Get the ID under which we registered this strategy for this tenant
    var strategyId = AuthenticationUtil.getStrategyId(req.tenant, AuthenticationConstants.providers.TWITTER);

    // Perform the initial authentication step
    AuthenticationUtil.handleExternalSetup(strategyId, null, req, res, next);
});

/**
 * @REST getAuthTwitterCallback
 *
 * Twitter will redirect the user to this URL after approval. Finish the
 * authentication process by attempting to obtain an access token. If
 * access was granted, the user will be logged in. Otherwise,
 * authentication has failed.
 *
 * @Api         private
 * @Server      tenant
 * @Method      POST
 * @Path        /auth/twitter/callback
 */
OAE.tenantRouter.on('get', '/api/auth/twitter/callback', function(req, res, next) {
    // Get the ID under which we registered this strategy for this tenant
    var strategyId = AuthenticationUtil.getStrategyId(req.tenant, AuthenticationConstants.providers.TWITTER);

    // Log the user in
    AuthenticationUtil.handleExternalCallback(strategyId, req, res, next);
});
